(()=>{const t="http://localhost:3000";async function e(){const e=await fetch(`${t}/tickets`),n=await e.json(),c=document.getElementById("tickets");c.innerHTML="",n.forEach((t=>{const e=document.createElement("div");e.className="ticket",e.textContent=t.name,c.appendChild(e)}))}document.getElementById("app").textContent="HelpDesk загружен",document.getElementById("new-ticket").onclick=async()=>{const n=prompt("Название заявки:"),c=prompt("Описание заявки:");n&&(await fetch(`${t}/tickets`,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({name:n,description:c})}),e())},e(),console.log("Приложение запущено")})();